def dfs(adj):
    for i in adj:
        if visited[i] == False:
            explore(i,adj,visited)
    return

def explore(v,adj,visited):
    visited[v] = True
    for i in adj[v]:
        if visited[i] == False:
            explore(i,adj,visited)
    v.strip()
    post.append(v)
    return visited

def hamiltonian(adj,visited,post):
    for item in visited:
        visited[item] = False
    dfs(adj)
    return post

def overlap(a,b):
    #a = a[1:]
    i = 1
    j = 0
    k = 0
    while (i < len(a) and j < len(b)):
        if a[i] == b[j]:
            while (i < len(a) and j < len(b)) and a[i] == b[j]:
                i += 1
                j += 1
                k += 1
            break
        i += 1

    return j
def check_path(path,edges,adj):
    visited = dict()
    for item in adj:
        visited[item] = False
    j = 0
    while j < len(path) - 1:
        if path[j+1] in adj[path[j]]:
            visited[path[j+1]] = True
            visited[path[j]] = True
            j += 1
        else:
            return False
    if False not in visited.values():
        return True
    else:
        return False
